public int maxTriple(int[] nums){  int a=0;  if(nums.length==1)  return nums[0];   if(nums.length==2)  if(nums[0]>nums[1])  a=nums[0];  else  a=nums[1];   if(nums[0]>nums[nums.length/2]&&nums[0]>nums[nums.length-1])  a=nums[0];  else if(nums[nums.length/2]>nums[0]&&nums[nums.length/2]>nums[nums.length-1])  a=nums[nums.length/2];  else if(nums[nums.length-1]>nums[0]&&nums[nums.length/2]<nums[nums.length-1])  a=nums[nums.length-1];  return a; }