public String withoutX2(String str){ if(str.equals("x")) return "";  if(str.length()<2) return str;  if(str.startsWith("xx")|| str.equals("xx")) return str.substring(2);  if(str.charAt(0)=='x') return str.substring(1);  if(str.charAt(1)=='x') return str.substring(0,1)+str.substring(2);  return str;}